package com.memi.web.dto;

import java.io.Serializable;

public class ColorDto implements Serializable {

	private static final long serialVersionUID = 1L;
	private int colSeq;
	private String color;
	
	
	
	public ColorDto() {
		super();
		// TODO Auto-generated constructor stub
	}
	public String getColor(int colorSeq) {
		color = String.valueOf(colorSeq);
		return color;
	}
	/**
	 * @return the colSeq
	 */
	public int getColSeq() {
		return colSeq;
	}
	/**
	 * @return the color
	 */
	public String getColor() {
		return color;
	}
	/**
	 * @param colSeq the colSeq to set
	 */
	public void setColSeq(int colSeq) {
		this.colSeq = colSeq;
	}
	/**
	 * @param color the color to set
	 */
	public void setColor(String color) {
		this.color = color;
	}
	/* (non-Javadoc)
	 * @see java.lang.Object#toString()
	 */
	@Override
	public String toString() {
		return "ColorDto [colSeq=" + colSeq + ", color=" + color + "]";
	}
	
	
	
	

}
